        <Table  striped bordered hover responsive>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
          {users.map((user) =>(
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
              </tr>
            ))}
          </tbody>
        </Table>
        </Col>
      </Row>     
    </Container>

  );
}
export default Dashboard;
